package day01;

public class 타입_실수 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double a = 10;
		double b = 3;
		
		double c = a + b; // 13.0
		double d = a - b; // 7.0 
		double e = a * b; // 30.0
		double f = a / b; // 3.3333333333335
		double g = a % b; // 1.0
		// double는 실수타입이다. 그래서 소수점까지 보여준다.
		
		System.out.println(c);
		System.out.println(d);
		System.out.println(e);
		System.out.println(f);
		System.out.println(g);
		
		
	}

}
